soup = BeautifulSoup(html_tabela, 'html.parser')

vereadores = []
for vereador in soup.find_all('tbody'):
  dados = []
  dados.append(vereador.a.text) #nome
  dados.append(vereador('th')[1].text) #partido
  dados.append(vereador('th')[2].text) #ativo
  dados.append(vereador('th')[3].text) #titular
  dados.append(vereador.a.get('href')) #link
  vereadores.append(dados)
vereadores

df = pd.DataFrame(vereadores, columns=['Nome', 'Partido','Ativo', 'Titular','Link'])
df.to_csv('vereadores.csv', index=False)